import React, { useState } from "react";
import { HashLink } from "react-router-hash-link"; // Import HashLink
import { useNavigate } from "react-router-dom"; // Importing useNavigate
import "./meniu.css";

function Meniu() {
    const navigate = useNavigate();
    const [menuOpen, setMenuOpen] = useState(true); // Track if the menu is open
    const [isClosing, setIsClosing] = useState(false);
    const handleCloseMenu = () => {
        setMenuOpen(false); // Close the menu
        setTimeout(() => {
            navigate("/"); // Go to the main page after animation
        }, 500); // Adjust timing with animation duration
    };

    return (
        <div className={`meniu-overlay ${!menuOpen ? "hidden" : ""}`}>

            <button className="close-x" onClick={handleCloseMenu}>
            <svg width="53" height="14" viewBox="0 0 53 14" fill="none" xmlns="http://www.w3.org/2000/svg">
<line y1="-0.5" x2="53.3925" y2="-0.5" transform="matrix(-0.973919 0.226895 -0.234088 -0.972215 52 0)" stroke="white"/>
</svg>

            </button>
            <div className="meniu-content">
                <h2
                    className="menu-item"
                    onClick={() => {
                        navigate("/apieMane");
                    }}
                >
                    Apie mane
                </h2>
                <h2 className="menu-item" onClick={handleCloseMenu}>
                    <HashLink smooth to="/#mano-darbai" className="no-link-style">
                        Mano darbai
                    </HashLink>
                </h2>
                <h2>
                    <span className="flower">
                        <img
                            className="gele_s"
                            src="./nuotraukos/gelyte.png"
                            alt="Flower"
                        />
                    </span>
                    BENDRADARBIAUJAM?{" "}
                    <span className="flower">
                        <img
                            className="gele_s"
                            src="./nuotraukos/gelyte.png"
                            alt="Flower"
                        />
                    </span>
                </h2>
            </div>
        </div>
    );
}



export default Meniu;
