import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { HashLink } from "react-router-hash-link"; // Import HashLink
import { Link } from "react-router-dom"; // Import Link component from react-router-dom
import "./Pagrindinis_d.css";

const Header = () => {
    const [menuOpen, setMenuOpen] = useState(false);
    const navigate = useNavigate();

    const openMenu = () => setMenuOpen(true);
    const closeMenu = () => setMenuOpen(false);

    return (
        <header className="header">
             <Link to="/"> {/* Link component wraps your logo */}
            <img className="logo" src="/nuotraukos/logo.png" alt="Logo" />
            </Link>
            <div className="hamburger-menu" onClick={openMenu}>
                <span></span>
                <span></span>
                <span></span>
            </div>
            {menuOpen && <Meniu onClose={closeMenu} />}
        </header>
    );
};

const Meniu = ({ onClose }) => {
    const navigate = useNavigate();

    const handleNavigate = (path) => {
        onClose(); // Close the menu first
        navigate(path); // Navigate to the selected path
    };

    return (
        <div className="meniu-overlay">
            <div className="meniu-content slide-in">
                <button className="close-x" onClick={onClose}>
                    X
                </button>
                <h2 className="menu-item" onClick={() => handleNavigate("/apieMane")}>
                    Apie mane
                </h2>
                <HashLink
                    smooth
                    to="/#mano-darbai"
                    className="menu-item no-link-style"
                    onClick={handleCloseMenu}
                >
                    Mano darbai
                </HashLink>
                <h3>BENDRADARBIAUJAM?</h3>
            </div>
        </div>
    );
};

export default Header;
