import React, { useEffect, useRef } from "react";
import "./dizainoDarbai.css";

const DizainoDarbai = () => {
  // Sample data for design works

  
  const designs = [
    {
      id: 1,
      imgSrc: "/nuotraukos/grafinis1.png", // Replace with your image paths
      title: "Design Work 1",
      description: "A beautiful poster design for an art workshop.",
    },
    {
      id: 2,
      imgSrc: "/nuotraukos/grafinis2.png", // Replace with your image paths
      title: "Design Work 2",
      description: "An engaging and modern event flyer design.",
    },
    {
      id: 3,
      imgSrc: "/nuotraukos/grafinis3.png", // Replace with your image paths
      title: "Design Work 3",
      description: "A creative illustration for a theater performance.",
    },
    {
      id: 4,
      imgSrc: "/nuotraukos/grafinis4.png", // Replace with your image paths
      title: "Design Work 4",
      description: "A unique brand logo for a tech company.",
    },
    {
      id: 5,
      imgSrc: "/nuotraukos/grafinis5.png", // Replace with your image paths
      title: "Design Work 5",
      description: "An artistic flyer design for a music event.",
    },
  ];

  const galleryRef = useRef(null);

  useEffect(() => {
    const handleWheel = (event) => {
      const gallery = galleryRef.current;

      // Only apply this on desktop and larger devices
      if (gallery && window.innerWidth > 768) {
        // Map vertical scroll to horizontal scroll
        if (event.deltaY !== 0) {
          event.preventDefault(); // Prevent default scroll behavior
          gallery.scrollLeft += event.deltaY; // Scroll horizontally
        }
      }
    };

    // Attach wheel event listener
    if (galleryRef.current) {
      galleryRef.current.addEventListener("wheel", handleWheel, { passive: false });
    }

    // Cleanup on component unmount
    return () => {
      if (galleryRef.current) {
        galleryRef.current.removeEventListener("wheel", handleWheel);
      }
    };
  }, []);

  return (

  

    <div className="dizaino-darbai-container">
      
        <button className="closex" onClick={handleClose} >
    <svg width="53" height="14" viewBox="0 0 53 14" fill="none" xmlns="http://www.w3.org/2000/svg">
<line y1="-0.5" x2="52.2738" y2="-0.5" transform="matrix(-0.968583 -0.248689 0.256484 -0.966549 52 13)" stroke="black"/>
<line y1="-0.5" x2="53.3925" y2="-0.5" transform="matrix(-0.973919 0.226895 -0.234088 -0.972215 52 0)" stroke="black"/>
</svg>

    </button>
      <h1>Grafinio Dizaino Darbai</h1>
      <div className="design-gallery" ref={galleryRef}>
        {designs.map((design) => (
          <div className="design-card" key={design.id}>
            <img
              src={design.imgSrc} // Correctly using imgSrc here
              alt={design.title}
              className="design-image"
            />
            <h2 className="design-title">{design.title}</h2>
            <p className="design-description">{design.description}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default DizainoDarbai;
