import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Pagrindinis_d.css";

const Header = () => {
    const [menuOpen, setMenuOpen] = useState(false);
    const navigate = useNavigate();

    const openMenu = () => {
        setMenuOpen(true);
        // Add a delay for the animation before navigating
        setTimeout(() => {
            navigate("/meniu");
        }, 800); // Matches the animation duration in CSS
    };

    return (
        <header className="header">
            <img className="logo" src="/nuotraukos/logo.png" alt="Logo" />
            <div className="hamburger-menu" onClick={openMenu}>
                <span></span>
                <span></span>
                <span></span>
            </div>
            {menuOpen && <div className="menu-overlay"></div>}
        </header>
    );
};

export default Header;
