import React, { useEffect, useRef } from "react";
import "./Apie_mane.css";

const HorizontalScrollPage = () => {
  const scrollContainer = useRef(null);

  // Handle mouse wheel scrolling (vertical -> horizontal)
  const handleWheel = (event) => {
    event.preventDefault(); // Prevent the default vertical scrolling
    scrollContainer.current.scrollLeft += event.deltaY; // Translate vertical wheel to horizontal scroll
  };

  // Handle touchpad gestures (two-finger swipe for horizontal scroll)
  const handleTouchMove = (event) => {
    event.preventDefault();
    const touchStartX = event.touches[0].clientX; // Get the X coordinate of the touch start
    const touchEndX = event.touches[1] ? event.touches[1].clientX : touchStartX; // Get the X coordinate for a two-finger touch

    // Translate the touch movement into horizontal scroll
    const deltaX = touchStartX - touchEndX;
    scrollContainer.current.scrollLeft += deltaX;
  };

  useEffect(() => {
    const container = scrollContainer.current;

    // Add event listeners for mouse wheel and touch gestures
    container.addEventListener("wheel", handleWheel, { passive: false });
    container.addEventListener("touchmove", handleTouchMove, { passive: false });

    return () => {
      container.removeEventListener("wheel", handleWheel);
      container.removeEventListener("touchmove", handleTouchMove);
    };
  }, []);

  return (
    <div className="horizontal-page" ref={scrollContainer}>
      {/* Section 1: Text Content */}
      <section className="scroll-section text-section">
        <div className="content">
          <h1>Labas, aš Lėja.</h1>
          <p>Esu grafikos dizainerė ir iliustratorė.</p>
          <p>
            Todėl šiuo metu noriu pažinti žmones, atsidurti nepatogiose situacijose, kuriose galėčiau išbandyti save ir išmokti ką nors naujo.
          </p>
          <p>
            Man labai patinka kūryba, todėl nenoriu savęs apibrėžti tik dviem žodžiais – geriau išbandyti ir mokėti daug dalykų.
          </p>
        </div>
      </section>

      {/* Section 2: Sketch Images */}
      <section className="scroll-section image-section">
        <div className="images">
          <img src="/path/to/sketch1.jpg" alt="Sketch 1" />
          <img src="/path/to/sketch2.jpg" alt="Sketch 2" />
          <img src="/path/to/sketch3.jpg" alt="Sketch 3" />
          <img src="/path/to/sketch4.jpg" alt="Sketch 4" />
        </div>
      </section>
    </div>
  );
};

export default HorizontalScrollPage;
