import React, { useEffect, useRef } from "react";
import "./Apie_mane.css";

const HorizontalScrollPage = () => {
    const scrollContainer = useRef(null);

    useEffect(() => {
        const container = scrollContainer.current;

        const handleWheel = (event) => {
            // Check if the user is scrolling vertically (deltaY > 0)
            if (event.deltaY !== 0) {
                event.preventDefault();
                container.scrollLeft += event.deltaY; // Map vertical scroll to horizontal scroll
            }
        };

        container.addEventListener("wheel", handleWheel, { passive: false });

        return () => {
            container.removeEventListener("wheel", handleWheel);
        };
    }, []);

    return (
        <div className="horizontal-page" ref={scrollContainer}>
            {/* Section 1: Text Content */}
            <section className="scroll-section text-section">
                <div className="content">
                    <h1>Labas, aš Lėja.</h1>
                    <p>Esu grafikos dizainerė ir iliustratorė.</p>
                    <p>
                    Man labai patinka kūryba, todėl nenoriu savęs apibrėžti tik dviem žodžiais – 
                    geriau išbandyti ir mokėti daug dalykų vidutiniškai nei siekti tobulybės viename.
                    Paklausite kodėl? Nes gebėjimas pritaikyti įvairius įgudžius suteikia lankstumo 
                    ir originalumo sprendžiant bet kokius iššūkius.
                    </p>
                    <p>
                    Todėl šiuo metu noriu pažinti žmones, atsidurti nepatogiose situacijose, 
                    kuriose galėčiau išbandyti save ir išmokti ką nors naujo. Kadangi dar studijuoju ir semiuosi dėstytojų išminties, nesu jokia ekspertė ir savo srityje, bet aš manau, kad svarbu bandyt ir klyst nei iš vis nebandyt.
                    </p>
                </div>
            </section>

            {/* Section 2: Sketch Images */}
            <section className="scroll-section image-section">
                <div className="images">
                    <img src="/path/to/sketch1.jpg" alt="Sketch 1" />
                    <img src="/path/to/sketch2.jpg" alt="Sketch 2" />
                    <img src="/path/to/sketch3.jpg" alt="Sketch 3" />
                    <img src="/path/to/sketch4.jpg" alt="Sketch 4" />
                </div>
            </section>
        </div>
    );
};

export default HorizontalScrollPage;
