import React, { useRef } from "react";
import "./maketai.css";

function Maketai() {
    const scrollContainerRef = useRef(null);

    const handleMouseDrag = (event) => {
        if (scrollContainerRef.current) {
            const container = scrollContainerRef.current;
            let isDragging = false;
            let startX, scrollLeft;

            const mouseDown = (e) => {
                isDragging = true;
                startX = e.pageX - container.offsetLeft;
                scrollLeft = container.scrollLeft;
                container.classList.add("dragging");
            };

            const mouseMove = (e) => {
                if (!isDragging) return;
                e.preventDefault();
                const x = e.pageX - container.offsetLeft;
                const walk = x - startX;
                container.scrollLeft = scrollLeft - walk;
            };

            const mouseUp = () => {
                isDragging = false;
                container.classList.remove("dragging");
            };

            container.addEventListener("mousedown", mouseDown);
            container.addEventListener("mousemove", mouseMove);
            container.addEventListener("mouseup", mouseUp);
            container.addEventListener("mouseleave", mouseUp);

            return () => {
                container.removeEventListener("mousedown", mouseDown);
                container.removeEventListener("mousemove", mouseMove);
                container.removeEventListener("mouseup", mouseUp);
                container.removeEventListener("mouseleave", mouseUp);
            };
        }
    };

    return (
        <div className="maketai-container">
            {/* Cover Section */}
            <div className="section cover">
                <h1>PORTFOLIO</h1>
            </div>

            {/* Horizontal Scrolling Image with Drag */}
            <div
                className="image-scroll-container"
                ref={scrollContainerRef}
                onMouseEnter={handleMouseDrag}
            >
                <img src="/nuotraukos/portfolio.png" alt="PaveikslÄ—lis" />
            </div>
        </div>
    );
}

export default Maketai;
